import React from "react";
import { useState, useRef } from "react";
import { connect } from "react-redux";
import { setTypedMessage, setEditing, deleteMessage } from "../actions";
//parent: ChatMessages
const ChatMessage = ({
  activeId,
  message,
  index,
  setTypedMessage,
  setEditing,
  deleteMessage,
}) => {
  const el = useRef(null);
  const [isDeleteVisible, setDeleteVisible] = useState(false);
  const { text, is_user } = message;
  const handleOnclick = (e) => {
    if (message.is_user) {
      //only allow update if it's user's message
      setTypedMessage(message.text); //to edit message
      setEditing(index);
    }
  };
  const handleRightClick = (e) => {
    e.preventDefault();
    if (message.is_user) {
      setDeleteVisible(true);
    }
  };

  const handleDelete = (e) => {
    e.preventDefault();
    deleteMessage(index, activeId);
  };
  return (
    <div
      ref={el}
      onClick={() => {
        setDeleteVisible(false);
      }}
    >
      <span
        className={`chat ${is_user ? "is-user" : ""}`}
        onClick={handleOnclick}
        onContextMenu={handleRightClick} //contextmenu is triggered when user right-clicks
      >
        {text}
        {isDeleteVisible ? (
          <button className="delete-btn" onClick={handleDelete}>
            Delete
          </button>
        ) : null}
      </span>
    </div>
  );
};
const mapStateToProps = (state) => {
  return {
    activeId: state.activeId,
  };
};

export default connect(mapStateToProps, {
  setTypedMessage,
  setEditing,
  deleteMessage,
})(ChatMessage);
