import React from "react";
import { connect } from "react-redux";
import { setTypedMessage, sendMessage, updateMessage } from "../actions";

//parent: ChatWindow
const ChatInput = ({
  typedMessage,
  activeId,
  setTypedMessage,
  sendMessage,
  updateMessage,
  editing,
}) => {
  const handleSubmit = (e) => {
    e.preventDefault(); //to prevent page to reload after form is submitted
    //if message is new message (not edited)
    if (!editing) {
      sendMessage(typedMessage, activeId);
    } else {
      updateMessage(editing.text, editing.conversationId, editing.messageId);
    }
  };
  const handleChange = (e) => {
    setTypedMessage(e.target.value);
  };
  return (
    <form className="chat-form" onSubmit={handleSubmit}>
      <input
        className="chat-input"
        onChange={handleChange}
        value={typedMessage}
        placeholder="Type a message"
      />
    </form>
  );
};

const mapStateToProps = (state) => {
  return {
    typedMessage: state.typedMessage,
    activeId: state.activeId,
    editing: state.editing,
  };
};

export default connect(mapStateToProps, {
  setTypedMessage,
  sendMessage,
  updateMessage,
})(ChatInput);
