import React, { useState } from "react";
import Contact from "../containers/Contact";

//parent: App.js
const ChatList = ({ contacts }) => {
  const [isAddPersonVisible, setAddPersonVisible] = useState(false);
  const handleClick = () => {
    setAddPersonVisible(true);
  };
  const handleSubmit = (e) => {
    e.preventDefault();
    console.log(e.target.name.value);
    console.log(e.target.status.value);
    console.log(e.target.details.value);
  };
  return (
    <>
      <div className="chat-list">
        {contacts.map((contact) => (
          <Contact contact={contact} key={contact.user_id} />
        ))}
        <div style={{ flex: 1 }}></div>
        <h3
          style={{ margin: "0 auto 1em auto", color: "purple" }}
          onClick={handleClick}
        >
          + Add a person
        </h3>
      </div>
      <form
        style={{
          position: "absolute",
          bottom: "0px",
          left: "320px",
          display: "flex",
          flexDirection: "column",
        }}
        onSubmit={handleSubmit}
      >
        <input name="name" placeholder="add name"></input>
        <input name="status" placeholder="add status"></input>
        <input name="details" placeholder="add details"></input>
        <button type="submit">Submit</button>
      </form>
    </>
  );
};

export default ChatList;
