import data from "../data";
import { SET_ACTIVE_ID } from "../constants/actions";
const activeId = (state = data.activeId, action) => {
  switch (action.type) {
    case SET_ACTIVE_ID: {
      return { ...state, activeId: action.id };
    }
    default:
      return state;
  }
};

export default activeId;
