//send data to activeId reducer
export const setActiveId = (id) => ({
  type: "SET_ACTIVE_ID",
  id,
});

//send data to typedMessage reducer
export const setTypedMessage = (message) => ({
  type: "SET_TYPED_MESSAGE",
  message,
});

//send data to messages reducer
export const sendMessage = (message, messageId) => ({
  type: "SEND_MESSAGE",
  message,
  messageId,
});

//send data to messages reducer
export const updateMessage = (editedText, activeChatId, messageId) => ({
  type: "UPDATE_MESSAGE",
  editedText,
  activeChatId,
  messageId,
});

//send data to editing reducer
export const setEditing = (editedText, activeChatId, messageId) => ({
  type: "SET_EDITING",
  editedText,
  activeChatId,
  messageId,
});
