import React from "react";
import { connect } from "react-redux";
import { setTypedMessage, updateMessage } from "../actions";
//parent: ChatMessages
const ChatMessage = ({
  messages,
  activeId,
  message,
  setTypedMessage,
  index,
}) => {
  const { text, is_user } = message;
  const messageToUpdate = messages[activeId][index];

  const handleOnclick = (e) => {
    if (message.is_user) {
      //only allow update if it's user's message
      setTypedMessage(message.text); //to edit message
      console.log(message);
      console.log(index);
      updateMessage(messageToUpdate);
    }
  };
  return (
    <span
      className={`chat ${is_user ? "is-user" : ""}`}
      onClick={handleOnclick}
    >
      {text}
    </span>
  );
};
const mapStateToProps = (state) => {
  return {
    activeId: state.activeId,
    messages: state.messages,
  };
};

export default connect(mapStateToProps, { setTypedMessage })(ChatMessage);
