const editing = (state = null, action) => {
  switch (action.type) {
    case "SET_EDITING": {
      return {
        text: action.editedText,
        conversationId: action.activeChatId,
        messageId: action.messageId,
      };
    }
    case "UPDATE_MESSAGE": {
      //to clear out the editing after user hits enter
      return null;
    }
    default:
      return state;
  }
};

export default editing;
