import React from "react";
import { useState } from "react";
import { connect } from "react-redux";
import { setTypedMessage, setEditing, deleteMessage } from "../actions";
//parent: ChatMessages
const ChatMessage = ({
  messages,
  activeId,
  message,
  index,
  setTypedMessage,
  setEditing,
  deleteMessage,
}) => {
  const [isDeleteVisible, setDeleteVisible] = useState(false);
  const { text, is_user } = message;
  const handleOnclick = (e) => {
    if (message.is_user) {
      //only allow update if it's user's message
      setTypedMessage(message.text); //to edit message
      console.log(message);
      console.log(index);
      //dispatch action and send the edited message, active conversation id and message id
      setEditing(index);
    }
  };
  const handleRightClick = (e) => {
    e.preventDefault();
    if (message.is_user) {
      setDeleteVisible(true);
    }
  };

  const handleDelete = (e) => {
    e.preventDefault();
    deleteMessage(index, activeId);
  };
  return (
    <>
      {isDeleteVisible ? (
        <button className="delete-btn" onClick={handleDelete}>
          Delete
        </button>
      ) : null}
      <span
        className={`chat ${is_user ? "is-user" : ""}`}
        onClick={handleOnclick}
        onContextMenu={handleRightClick} //contextmenu is triggered when user right-clicks
      >
        {text}
      </span>
    </>
  );
};
const mapStateToProps = (state) => {
  return {
    activeId: state.activeId,
    messages: state.messages,
  };
};

const mapDispatchToProps = (state) => {
  return {
    setTypedMessage: state.setTypedMessage,
    setEditing: state.setEditing,
    deleteMessage: state.deleteMessage,
  };
};
export default connect(mapStateToProps, mapDispatchToProps)(ChatMessage);
