import data from "../data";

const editing = (state = data.editing, action) => {
  switch (action.type) {
    case "SET_EDITING": {
      console.log(action.ditedText);
      return {
        text: action.editedText,
        conversationId: action.activeChatId,
        messageId: action.messageId,
      };
    }
    default:
      return state;
  }
};

export default contacts;
