export const setActiveId = (id) => ({
  type: "SET_ACTIVE_ID",
  id,
});

export const setTypedMessage = (message) => ({
  type: "SET_TYPED_MESSAGE",
  message,
});

export const sendMessage = (message, id) => ({
  type: "SEND_MESSAGE",
  message,
  id,
});

export const updateMessage = (message, id, edited) => {
  type: "UPDATE_MESSAGE", message, id, edited;
};
