import React from "react";
import { setActiveId } from "../actions";
import { connect } from "react-redux";

const Contact = ({ contact, activeId }) => {
  //activeId part of the state Contact can use as prop thanks to mapStateToProps
  const { pic, name, status } = contact;
  const handleClick = () => {
    console.log(contact.user_id);
    props.setActiveId(contact.user_id); //dispatch action
  };
  return (
    <div className="contact" onClick={handleClick}>
      <img src={pic} alt={name} />
      <div className="contact-details">
        <p className="contact-name">{name}</p>
        <p className="contact-status">{status}</p>
      </div>
    </div>
  );
};
const mapStateToProps = (state) => {
  return {
    activeId: state.activeId,
  };
};
export default connect(mapStateToProps, { setActiveId })(Contact);
