import React from "react";
import Contact from "../containers/Contact";

//parent: App.js
const ChatList = ({ contacts, activeId }) => {
  return (
    <div className="sidebar">
      {contacts.map((contact) => {
        return (
          <Contact
            className={`sidebar ${
              activeId === contact.user_id ? "active" : ""
            }`}
            contact={contact}
            key={contact.user_id}
          />
        );
      })}
    </div>
  );
};

export default ChatList;
