import React from "react";
import { useState, useRef } from "react";
import { connect } from "react-redux";
import { setTypedMessage, setEditing, deleteMessage } from "../actions";
//parent: ChatMessages
const ChatMessage = ({
  activeId,
  message,
  index,
  setTypedMessage,
  setEditing,
  deleteMessage,
}) => {
  const [isDeleteVisible, setDeleteVisible] = useState(false);
  const { text, is_user } = message;
  const del = useRef(null);
  const handleOnclick = (e) => {
    if (e.currentTarget === del.current) {
      return;
    }
    if (message.is_user) {
      //only allow update if it's user's message
      setTypedMessage(message.text); //to edit message
      setEditing(index);
    }
  };
  const handleHover = (e) => {
    e.preventDefault();
    if (message.is_user) {
      setDeleteVisible(true);
    }
  };
  const handleUnhover = (e) => {
    e.preventDefault();
    if (message.is_user) {
      setDeleteVisible(false);
    }
  };

  const handleDelete = (e) => {
    e.preventDefault();
    if (message.is_user) {
      deleteMessage(index, activeId);
    }
  };
  return (
    <>
      <span
        className={`chat ${is_user ? "is-user" : ""}`}
        onClick={handleOnclick}
        onMouseEnter={handleHover}
        onMouseLeave={handleUnhover}
      >
        {text}
        {isDeleteVisible ? (
          <button className="delete-btn" onClick={handleDelete} ref={del}>
            ğŸ—‘ï¸
          </button>
        ) : null}
      </span>
    </>
  );
};
const mapStateToProps = (state) => {
  return {
    activeId: state.activeId,
  };
};

export default connect(mapStateToProps, {
  setTypedMessage,
  setEditing,
  deleteMessage,
})(ChatMessage);
