import data from "../data";

const messages = (state = data.messages, action) => {
  const existingMessages = state[action.messagseId];
  switch (action.type) {
    case "SEND_MESSAGE": {
      //create a new conversation if id is new
      if (!existingMessages) {
        return {
          ...state,
          [action.id]: [
            {
              is_user: true,
              text: action.message,
            },
          ],
        };
      } else {
        //add message to existing conversation
        return {
          ...state,
          [action.id]: [
            ...existingMessages, //preserve existing messages
            {
              is_user: true,
              text: action.message,
            },
          ],
        };
      }
    }
    case "UPDATE_MESSAGE": {
      //got from action: editedText, activeChatId, messageId,
      return {
        ...state,
        [action.activeChatId]: [
          ...state[action.activeChatId].splice(action.messageId),
          {
            is_user: true,
            text: action.editedText,
          },
          ...state[activeChatId].splice(action.activeChatId + 2),
        ],
      };
    }
    default:
      return state;
  }
};

export default messages;
