import React, { useState } from "react";
import Contact from "../containers/Contact";
import { connect } from "react-redux";
import { createPerson } from "../actions";

//parent: App.js
const ChatList = ({ contacts, createPerson }) => {
  const [isAddPersonVisible, setAddPersonVisible] = useState(false);

  const handleClick = () => {
    setAddPersonVisible((prev) => !prev);
  };
  const handleSubmit = (e) => {
    e.preventDefault();
    setAddPersonVisible(false);
    createPerson(
      e.target.name.value,
      e.target.status.value,
      e.target.details.value
    );
  };
  return (
    <>
      <div className="chat-list">
        {contacts.map((contact) => (
          <Contact contact={contact} key={contact.user_id} />
        ))}
        <div style={{ flex: 1 }}></div>
        <h3
          style={{ margin: "0 auto 1em auto", color: "purple" }}
          onClick={handleClick}
        >
          {isAddPersonVisible ? "< Close" : "+ Add a person"}
        </h3>
      </div>
      {isAddPersonVisible && (
        <form
          style={{
            position: "absolute",
            bottom: "0px",
            left: "330px",
            display: "flex",
            flexDirection: "column",
          }}
          onSubmit={handleSubmit}
        >
          <div>
            <span display="inline">
              <h5>Name: </h5>
            </span>
            <span display="inline">
              <input
                className="chat-input"
                name="name"
                placeholder="add name"
              ></input>
            </span>
          </div>
          <div>
            <h5>Status: </h5>
            <input
              className="chat-input"
              name="status"
              placeholder="add status"
            ></input>
          </div>
          <div>
            <h5>Details: </h5>
            <input
              className="chat-input"
              name="details"
              placeholder="add details"
            ></input>
          </div>
          <button type="submit" className="input-submit">
            Submit
          </button>
        </form>
      )}
    </>
  );
};

export default connect(null, { createPerson })(ChatList);
