import data from "../data";

const typedMessage = (state = data.typedMessage && data.activeId, action) => {
  switch (action.type) {
    case "SET_TYPED_MESSAGE": {
      return action.message;
    }
    case "SEND_MESSAGE": {
      //to clear out the typedMessage after user hits enter
      return "";
    }
    case "UPDATE_MESSAGE":
      return "";
    default:
      return state;
  }
};

export default typedMessage;
