import React from "react";

//parent: MainWindow
const ContactViewDetail = ({ contact, activeId }) => {
  const { name, pic, status } = contact;

  const handleClick = () => {
    if (activeId !== contact.user_id) {
      console.log(contact.user_id);
      setActiveId(contact.user_id); //dispatch action
    }
  };

  return (
    <div className="welcome-window">
      <img src={pic} alt="" />
      <h1>Welcome {name}</h1>
      <h3>Status: {status}</h3>
      <button className="message-btn" onClick={handleClick}>
        Message {name}
      </button>
    </div>
  );
};

export default ContactViewDetail;
