export const setActiveId = (id) => ({
  type: "SET_ACTIVE_ID",
  id,
});

export const setTypedMessage = (message) => ({
  type: "SET_TYPED_MESSAGE",
  message,
});

export const sendMessage = (message, messageId) => ({
  type: "SEND_MESSAGE",
  message,
  messageId,
});

export const updateMessage = (editedmessage, activeChatId, messageId) => ({
  type: "UPDATE_MESSAGE",
  editedmessage,
  activeChatId,
  messageId,
});

export const setEditing = (text, activeChatId, messageId) => ({
  type: "SET_EDITING",
  editedmessage,
  activeChatId,
  messageId,
});
