import { SET_ACTIVE_ID } from "../constants/actions";
import { SET_TYPED_MESSAGE } from "../constants/actions";
import { SEND_MESSAGE } from "../constants/actions";
export const setActiveId = (id) => ({
  type: SET_ACTIVE_ID,
  id,
});

export const setTypedMessage = (message) => ({
  type: SET_TYPED_MESSAGE,
  message,
});

export const sendMessage = () => ({
  type: SEND_MESSAGE,
  message,
  id,
});
