import data from "../data";
import { SEND_MESSAGE } from "../constants/actions";

const messages = (state = data.messages, action) => {
  switch (action.type) {
    case SEND_MESSAGE: {
      //create a new conversation if id is new
      if (!state[action.id]) {
        return {
          ...state,
          [action.id]: [
            {
              is_user: true,
              text: action.message,
            },
          ],
        };
      } else {
        //add message to existing conversation
        return {
          ...state,
          [action.id]: [],
        };
      }
    }
    default:
      return state;
  }
};

export default messages;
