import React from "react";
import Contact from "../containers/Contact";

//parent: App.js
const ChatList = ({ contacts, activeId }) => {
  return (
    <div className="sidebar">
      {contacts.map((contact) => (
        <Contact
          className={activeId ? "activeChat" : ""}
          contact={contact}
          key={contact.user_id}
        />
      ))}
    </div>
  );
};

export default ChatList;
