import data from "../data";

const messages = (state = data.messages, action) => {
  const existingMessages = state[action.id];
  switch (action.type) {
    case "SEND_MESSAGE": {
      //create a new conversation if id is new
      if (!existingMessages) {
        return {
          ...state,
          [action.id]: [
            {
              is_user: true,
              text: action.message,
              edited: false,
            },
          ],
        };
      } else {
        //add message to existing conversation
        return {
          ...state,
          [action.id]: [
            ...existingMessages, //preserve existing messages
            {
              is_user: true,
              text: action.message,
              edited: false,
            },
          ],
        };
      }
    }
    case "UPDATE_MESSAGE": {
      return {
        ...state,
        [action.id]: [...existingMessages, {}],
      };
    }
    default:
      return state;
  }
};

export default messages;
